<template>
    <div id="rating">
        <template v-if="full == 0">
            <span class="color_aaa">暂无评分</span>
        </template>
        <template v-else>
            <template v-for="n in full">
                <span class="star-full"></span>
            </template>
            <!--<template v-for="n in half">
                <span class="star-half"></span>
            </template>-->
            <template v-for="n in gray">
                <span class="star-gray"></span>
            </template>
            <span class="color_aaa">{{rating.average}}</span>
        </template>
    </div>
</template>
<script>
export default {
    props: ['rating'],
    data() {
        return {
            full: 0,
            // half: 0,
            gray: 0
        }
    },
    created() {
        let average = this.rating.average;
        let decimal = Number((average / 2).toString().replace((average / 2).toString().split(".")[0], "0")) >= 0.6 ? 1 : 0;  // 四舍五入判断：如果大于0.6则要进一
        this.full = parseInt(average / 2) + decimal;
        this.gray = 5 - this.full;
    }
}
</script>
