#rating {
    margin-top: .25rem;
}

.star-full {
    display: inline-block;
    width: .85rem;
    height: .85rem;
    background: url(data:image/png;base64,);
    -webkit-background-size: 100% 100%;
    background-size: 100% 100%;
    margin-right: .1rem;
}

.star-gray {
    display: inline-block;
    width: .85rem;
